$(function(){$(".featureBtn").click("click",function(e){e.preventDefault();$(".feature--inactive").toggleClass("feature--active")});$(".video__block--wrapper").on("click",function(){$(".video__youtube").addClass("video__youtube--show")})
$(window).scroll("load",function(){let st=$(this).scrollTop();let parallax=$(".feature").offset().top;if($(window).width()>=1200){$(".header").css({"background-position-y":"+"+st/3+"px"})}if($(window).scrollTop()>=parallax){$(".header").css({"background-position-y":"+"+st*0+"px"})}})})